styles:
.journal-layout {
    font-family: 'Raleway', sans-serif;
    font-weight: 500;
    font-size: 16px;
    overflow: hidden;
    box-sizing: border-box; }

.journal-layout * {
    box-sizing: border-box; }

.journal-layout p {
    margin: 0; }

.journal-cover {
    background: url(https://lti.skolkovo.ru/brand_static/journal-cover.png);
    background-size: cover;
    padding: 95px; }

.journal-cover__logo {
    display: block;
    margin-bottom: 95px; }

.journal-cover__description {
    color: #FFF;
    font-size: 20px;
    font-weight: 500;
    line-height: 28px;
    max-width: 630px;
    border-top: 2px solid #FFFFFF;
    padding-top: 110px;
    margin-bottom: 200px; }

.journal-cover__description p {
    margin-bottom: 24px; }

.journal-cover__title {
    color: #FFF;
    font-weight: 600;
    font-size: 36px;
    line-height: 36px;
    max-width: 360px;
    margin-bottom: 76px; }

.journal-cover__headline {
    color: #FFF;
    font-weight: bold;
    font-size: 113px;
    max-width: 630px;
    border-top: 2px solid #FFFFFF;
    margin: 0;
    padding-top: 65px; }

.journal-page {
    background: url(https://lti.skolkovo.ru/brand_static/journal-page.png);
    background-repeat: no-repeat;
    padding: 320px 95px 95px;
    position: relative; }

.journal-description {
    border-bottom: 2px solid #000000;
    font-size: 28px;
    margin: 0 0 0 auto;
    max-width: 840px;
    padding: 88px 0;
    position: relative; }

.journal-description::before {
    background-color: #C54D4D;
    content: '';
    display: block;
    height: 4px;
    position: absolute;
    left: 0;
    top: 0;
    width: 50%; }

.journal-answer {
    color: #534d4d;
    font-size: 20px;
    margin: 32px 0 0 auto;
    max-width: 840px; }

.journal-logo {
    left: 150px;
    position: absolute;
    top: 80px; }

.journal-back {
    background: url(https://lti.skolkovo.ru/brand_static/journal-back.png);
    background-position: bottom;
    background-repeat: no-repeat;
    color: #FFF;
    padding: 100px 95px 95px;
    position: relative; }

.journal-back__content {
    border-top: 2px solid #FFFFFF;
    border-bottom: 2px solid #FFFFFF;
    max-width: 680px;
    padding: 100px 0 100px; }

.journal-back__top {
    font-weight: 500;
    font-size: 28px;
    margin-bottom: 100px; }

.journal-back__bottom {
    font-weight: 500;
    font-size: 52px; }
